import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Container,
  Grid,
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Button,
  Typography,
  Alert,
  Box,
  CircularProgress
} from "@mui/material";
import RecipeCard from "../components/RecipeCard";

const API_URL = process.env.REACT_APP_API_URL;

const Recipes = () => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(true); // Added loading state
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("");
  const navigate = useNavigate();

  const fetchRecipes = useCallback(async (query = "", sort = "") => {
    setIsLoading(true); // Start loading
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }
      const response = await axios.get(
        `${API_URL}/recipes?search=${query}&sort=${sort}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRecipes(response.data);
    } catch (err) {
      setError("Failed to fetch recipes.");
    } finally {
      setIsLoading(false); // Stop loading, regardless of outcome
    }
  }, [navigate]);

  useEffect(() => {
    fetchRecipes();
  }, [fetchRecipes]);

  const handleSearchChange = (e) => setSearchQuery(e.target.value);
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    fetchRecipes(searchQuery, sortBy);
  };
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    fetchRecipes(searchQuery, e.target.value);
  };

  // This will log the data right before the component tries to render the list
  console.log("Data being rendered by Recipes.js:", recipes);

  return (
    <Container>
      <Typography variant="h4" gutterBottom sx={{ fontFamily: "'Patrick Hand', cursive", color: '#477491' }}>
        All Recipes
      </Typography>

      <form onSubmit={handleSearchSubmit} style={{ marginBottom: '30px', display: 'flex', alignItems: 'center', flexWrap: 'wrap' }}>
        {/* ... your form elements ... */}
      </form>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {isLoading ? (
        <Box display="flex" justifyContent="center" mt={4}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {recipes && recipes.map((recipe, index) => (
            <Grid item xs={12} sm={6} md={4} key={recipe?.id || index}>
              <RecipeCard recipe={recipe} />
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default Recipes;
