import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Container,
  Grid,
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Button,
  Typography,
  Alert,
  Box,
  CircularProgress,
  Autocomplete, // New Import
  Chip          // New Import
} from "@mui/material";
import RecipeCard from "../components/RecipeCard";

const API_URL = process.env.REACT_APP_API_URL;

const Recipes = () => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Search States
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("");
  const [selectedTags, setSelectedTags] = useState([]); // Stores user's selected tags
  const [availableTags, setAvailableTags] = useState([]); // Stores all tags from DB

  const navigate = useNavigate();

  // 1. Fetch Recipes (Now includes tags in the URL)
  const fetchRecipes = useCallback(async (query = "", sort = "", tags = []) => {
    setIsLoading(true);
    try {
      const token = localStorage.getItem("token");
      if (!token) { navigate("/login"); return; }

      // Convert array of tags ["Dinner", "Beef"] into string "Dinner,Beef"
      const tagsParam = tags.join(',');

      const response = await axios.get(
        `${API_URL}/recipes?search=${query}&sort=${sort}&tags=${tagsParam}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRecipes(response.data);
    } catch (err) {
      setError("Failed to fetch recipes.");
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  // 2. Fetch Available Tags on Mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) { navigate("/login"); return; }

    // Load Recipes
    fetchRecipes();

    // Load Tags
    axios.get(`${API_URL}/tags`, { 
        headers: { Authorization: `Bearer ${token}` } 
    }).then(res => {
        setAvailableTags(res.data);
    }).catch(err => console.error("Failed to load tags"));

  }, [fetchRecipes, navigate]);

  const handleSearchChange = (e) => setSearchQuery(e.target.value);
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    fetchRecipes(searchQuery, e.target.value, selectedTags);
  };

  // Handle Tag Selection
  const handleTagsChange = (event, newValue) => {
    setSelectedTags(newValue);
    // Optional: Trigger search immediately on tag change
    // fetchRecipes(searchQuery, sortBy, newValue);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    fetchRecipes(searchQuery, sortBy, selectedTags);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom sx={{ fontFamily: "'Patrick Hand', cursive", color: '#477491' }}>
        All Recipes
      </Typography>

      {/* SEARCH FORM */}
      <form onSubmit={handleSearchSubmit} style={{ marginBottom: '30px' }}>
        <Grid container spacing={2} alignItems="center">
            {/* Search Text */}
            <Grid item xs={12} md={4}>
                <TextField
                  label="Search Recipes"
                  variant="outlined"
                  fullWidth
                  value={searchQuery}
                  onChange={handleSearchChange}
                  size="small"
                />
            </Grid>

            {/* Tag Filter (New) */}
            <Grid item xs={12} md={4}>
                <Autocomplete
                    multiple
                    options={availableTags}
                    value={selectedTags}
                    onChange={handleTagsChange}
                    renderTags={(value, getTagProps) =>
                        value.map((option, index) => (
                            <Chip variant="outlined" label={option} size="small" {...getTagProps({ index })} />
                        ))
                    }
                    renderInput={(params) => (
                        <TextField {...params} variant="outlined" label="Filter by Tags" placeholder="Tags" size="small" />
                    )}
                />
            </Grid>

            {/* Sort Dropdown */}
            <Grid item xs={6} md={2}>
                <FormControl fullWidth size="small">
                  <InputLabel>Sort by</InputLabel>
                  <Select value={sortBy} label="Sort by" onChange={handleSortChange}>
                    <MenuItem value="">Default</MenuItem>
                    <MenuItem value="recipe_name_asc">Name (A-Z)</MenuItem>
                    <MenuItem value="recipe_name_desc">Name (Z-A)</MenuItem>
                    <MenuItem value="cuisine_asc">Cuisine (A-Z)</MenuItem>
                    <MenuItem value="total_time_asc">Time (Fastest)</MenuItem>
                    <MenuItem value="total_time_desc">Time (Slowest)</MenuItem>
                  </Select>
                </FormControl>
            </Grid>

            {/* Search Button */}
            <Grid item xs={6} md={2}>
                <Button 
                  variant="contained" 
                  type="submit" 
                  fullWidth
                  sx={{ backgroundColor: '#D28415', '&:hover': { backgroundColor: '#b36b10' } }}
                >
                  Search
                </Button>
            </Grid>
        </Grid>
      </form>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {isLoading ? (
        <Box display="flex" justifyContent="center" mt={4}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={2}>
          {recipes && recipes.map((recipe, index) => (
            <Grid size={{ xs: 12, sm: 6, md: 4 }} key={recipe?.id || index} sx={{ display: 'flex', justifyContent: 'center' }}>
              <RecipeCard recipe={recipe} />
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default Recipes;
