// src/components/RecipeCard.js

import React from 'react';
import { Card, CardHeader, CardMedia, CardContent, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import FavoriteButton from './FavoriteButton'; // <-- Import

const RecipeCard = ({ recipe }) => {
  const navigate = useNavigate();

  return (
    <Card sx={{ maxWidth: 345, cursor: 'pointer' }}>
      <CardHeader
        action={
          // Pass the recipe ID and initial favorite status to the button
          <FavoriteButton recipeId={recipe.id} isInitiallyFavorited={recipe.is_favorited} />
        }
        title={recipe.recipe_name}
        subheader={`Cuisine: ${recipe.cuisine}`}
        onClick={() => navigate(`/recipes/${recipe.id}`)}
      />
      <CardMedia
        component="img"
        height="194"
        image={recipe.images[0] || 'https://via.placeholder.com/345x194'}
        alt={recipe.recipe_name}
        onClick={() => navigate(`/recipes/${recipe.id}`)}
      />
      <CardContent onClick={() => navigate(`/recipes/${recipe.id}`)}>
        <Typography variant="body2" color="text.secondary">
          {recipe.description.substring(0, 100)}...
        </Typography>
      </CardContent>
    </Card>
  );
};

export default RecipeCard;
