import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  IconButton,
  CircularProgress,
  Alert
} from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';

const API_URL = process.env.REACT_APP_API_URL;

// You can customize this list with any measurements you want
const measurementOptions = [
  "tsp", "tbsp", "fl oz", "cup", "pint", "quart", "gallon",
  "ml", "l", "oz", "lb", "gram", "kg", "pinch", "dash"
];

const CreateRecipe = () => {
  const [recipeData, setRecipeData] = useState({
    recipe_name: "",
    description: "",
    cuisine: "",
    prep_time: "",
    cook_time: "",
    servings: "",
    difficulty: "Easy",
    ingredients: [{ ingredient_name: "", amount: "", measurement_name: "" }],
    steps: [{ step_number: 1, instruction: "" }],
    images: [""],
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const getUserId = () => {
    const token = localStorage.getItem("token");
    if (!token) return null;
    try {
      const decodedToken = jwtDecode(token);
      return decodedToken.sub;
    } catch (error) {
      console.error("Invalid token:", error);
      return null;
    }
  };

  const handleChange = (e) => {
    setRecipeData({ ...recipeData, [e.target.name]: e.target.value });
  };

  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...recipeData.ingredients];
    updatedIngredients[index][field] = value;
    setRecipeData({ ...recipeData, ingredients: updatedIngredients });
  };

  const handleStepChange = (index, value) => {
    const updatedSteps = [...recipeData.steps];
    updatedSteps[index].instruction = value;
    setRecipeData({ ...recipeData, steps: updatedSteps });
  };

  const handleImageChange = (index, value) => {
    const updatedImages = [...recipeData.images];
    updatedImages[index] = value;
    setRecipeData({ ...recipeData, images: updatedImages });
  };

  const addIngredientField = () => {
    setRecipeData({
      ...recipeData,
      ingredients: [...recipeData.ingredients, { ingredient_name: "", amount: "", measurement_name: "" }],
    });
  };

  const deleteIngredient = (index) => {
    const updatedIngredients = recipeData.ingredients.filter((_, i) => i !== index);
    setRecipeData({ ...recipeData, ingredients: updatedIngredients });
  };

  const addStepField = () => {
    setRecipeData({
      ...recipeData,
      steps: [...recipeData.steps, { step_number: recipeData.steps.length + 1, instruction: "" }],
    });
  };

  const deleteStep = (index) => {
    const updatedSteps = recipeData.steps.filter((_, i) => i !== index).map((step, i) => ({ ...step, step_number: i + 1 }));
    setRecipeData({ ...recipeData, steps: updatedSteps });
  };

  const addImageField = () => {
    setRecipeData({
      ...recipeData,
      images: [...recipeData.images, ""],
    });
  };

  const deleteImage = (index) => {
    const updatedImages = recipeData.images.filter((_, i) => i !== index);
    setRecipeData({ ...recipeData, images: updatedImages });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setIsSubmitting(true);
    const token = localStorage.getItem("token");

    const userId = getUserId();
    if (!userId) {
      setError("User not authenticated. Please log in again.");
      setIsSubmitting(false);
      return;
    }

    const submissionData = { 
        ...recipeData, 
        user_id: userId,
        images: recipeData.images.filter(img => img.trim() !== "")
    };

    try {
      const response = await axios.post(`${API_URL}/recipes`, submissionData, {
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      });

      if (response.status === 201) {
        navigate("/recipes");
      }
    } catch (err) {
      setError("Failed to create recipe. Please check all fields and try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Create a New Recipe
        </Typography>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        <Box component="form" onSubmit={handleSubmit} noValidate>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField name="recipe_name" label="Recipe Name" onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12}>
              <TextField name="description" label="Description" onChange={handleChange} fullWidth multiline rows={4} required />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField name="cuisine" label="Cuisine Type" onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Difficulty</InputLabel>
                <Select name="difficulty" value={recipeData.difficulty} label="Difficulty" onChange={handleChange}>
                  <MenuItem value="Easy">Easy</MenuItem>
                  <MenuItem value="Medium">Medium</MenuItem>
                  <MenuItem value="Hard">Hard</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField type="number" name="prep_time" label="Prep Time (mins)" onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField type="number" name="cook_time" label="Cook Time (mins)" onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField type="number" name="servings" label="Servings" onChange={handleChange} fullWidth required />
            </Grid>
          </Grid>

          <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>Ingredients</Typography>
          {recipeData.ingredients.map((ing, index) => (
            <Box key={index} sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
              <TextField label="Amount" value={ing.amount} onChange={(e) => handleIngredientChange(index, "amount", e.target.value)} sx={{ width: '20%' }} />

              {/* --- THIS IS THE UPDATED PART --- */}
              <FormControl sx={{ width: '30%' }}>
                <InputLabel>Unit</InputLabel>
                <Select
                  label="Unit"
                  value={ing.measurement_name}
                  onChange={(e) => handleIngredientChange(index, "measurement_name", e.target.value)}
                >
                  <MenuItem value=""><em>None</em></MenuItem>
                  {measurementOptions.map((option) => (
                    <MenuItem key={option} value={option}>{option}</MenuItem>
                  ))}
                </Select>
              </FormControl>
              {/* --- END OF UPDATE --- */}

              <TextField label="Ingredient Name" value={ing.ingredient_name} onChange={(e) => handleIngredientChange(index, "ingredient_name", e.target.value)} required fullWidth />
              <IconButton onClick={() => deleteIngredient(index)} color="error"><DeleteIcon /></IconButton>
            </Box>
          ))}
          <Button startIcon={<AddIcon />} onClick={addIngredientField}>Add Ingredient</Button>

          <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>Steps</Typography>
          {recipeData.steps.map((step, index) => (
            <Box key={index} sx={{ display: 'flex', alignItems: 'flex-start', gap: 2, mb: 2 }}>
              <Typography sx={{ pt: 2 }}>{index + 1}.</Typography>
              <TextField label={`Step ${index + 1}`} value={step.instruction} onChange={(e) => handleStepChange(index, e.target.value)} required fullWidth multiline />
              <IconButton onClick={() => deleteStep(index)} color="error" sx={{ mt: 1 }}><DeleteIcon /></IconButton>
            </Box>
          ))}
          <Button startIcon={<AddIcon />} onClick={addStepField}>Add Step</Button>

          <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>Images</Typography>
          {recipeData.images.map((image, index) => (
            <Box key={index} sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
              <TextField label="Image URL" value={image} onChange={(e) => handleImageChange(index, e.target.value)} fullWidth />
              <IconButton onClick={() => deleteImage(index)} color="error"><DeleteIcon /></IconButton>
            </Box>
          ))}
          <Button startIcon={<AddIcon />} onClick={addImageField}>Add Image</Button>

          <Box sx={{ mt: 4, textAlign: 'center' }}>
            <Button type="submit" variant="contained" size="large" disabled={isSubmitting} sx={{ minWidth: 200 }}>
              {isSubmitting ? <CircularProgress size={24} /> : "Submit Recipe"}
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default CreateRecipe;
