import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { Card, CardContent, Typography, Button, Grid, Box, Chip } from "@mui/material";
// --- 1. Import Share Buttons & Icons ---
import {
  FacebookShareButton,
  TwitterShareButton,
  EmailShareButton,
  WhatsappShareButton,
  FacebookIcon,
  TwitterIcon,
  EmailIcon,
  WhatsappIcon
} from "react-share";

const API_URL = process.env.REACT_APP_API_URL;

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Helper to get current URL for sharing
  const shareUrl = window.location.href;

  const checkAuth = () => {
    const token = localStorage.getItem("token");
    if (!token) return false;

    try {
      const decodedToken = jwtDecode(token);
      const currentTime = Date.now() / 1000;

      if (decodedToken.exp < currentTime) {
        console.warn("Token expired, redirecting to login.");
        localStorage.removeItem("token");
        localStorage.removeItem("refresh_token");
        return false;
      }

      return decodedToken;
    } catch (error) {
      console.error("Invalid token:", error);
      localStorage.removeItem("token");
      localStorage.removeItem("refresh_token");
      return false;
    }
  };

  useEffect(() => {
    const user = checkAuth();
    if (!user) {
        navigate("/login");
        return;
    }

    const fetchRecipe = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`${API_URL}/recipes/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setRecipe(response.data);
      } catch (err) {
        setError("Failed to fetch recipe.");
      }
    };

    fetchRecipe();
  }, [id, navigate]);

  const handleDelete = async () => {
    const confirmDelete = window.confirm("Are you sure you want to delete this recipe?");
    if (!confirmDelete) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_URL}/recipes/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Recipe deleted successfully!");
      navigate("/recipes");
    } catch (err) {
      setError("Failed to delete recipe.");
    }
  };

  if (!recipe) return <Typography sx={{ textAlign: 'center', marginTop: 4 }}>Loading...</Typography>;

  const currentUser = checkAuth();
  // Standardize ID access (handling 'sub' vs 'id' if necessary, usually 'sub' in standard JWTs)
  const currentUserId = currentUser ? parseInt(currentUser.sub || currentUser.id) : null;

  const isOwner = currentUserId === recipe.user_id;
  const isAdmin = currentUser && currentUser.role === "admin";

  const imageToShow = (recipe.images && recipe.images.length > 0) 
    ? recipe.images[0] 
    : "/no-image.png";

  const recipeTitle = `Check out this recipe for ${recipe.recipe_name}!`;

  return (
    <>
      {/* ðŸ”¹ Banner Image */}
      <Box
        sx={{
          height: 300,
          backgroundImage: `url(${imageToShow})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          position: 'relative',
          borderBottomLeftRadius: 8,
          borderBottomRightRadius: 8,
          boxShadow: 3
        }}
      >
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.4)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            borderBottomLeftRadius: 8,
            borderBottomRightRadius: 8
          }}
        >
          <Typography 
            variant="h3" 
            sx={{ 
              color: 'white', 
              fontFamily: "'Patrick Hand', cursive",
              textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
              textAlign: 'center',
              px: 2
            }}
          >
            {recipe.recipe_name}
          </Typography>
        </Box>
      </Box>

      {/* ðŸ”¹ Recipe Details */}
      <Card sx={{ maxWidth: 800, margin: "20px auto", padding: 2, borderRadius: 3, boxShadow: 3 }}>
        <CardContent>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
             {recipe.username && (
                <Typography variant="subtitle1" color="textSecondary">
                  Created by: {recipe.username}
                </Typography>
              )}

             {/* --- 2. Share Buttons UI --- */}
             <Box sx={{ display: 'flex', gap: 1 }}>
                <FacebookShareButton url={shareUrl} quote={recipeTitle}>
                  <FacebookIcon size={32} round />
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl} title={recipeTitle}>
                  <TwitterIcon size={32} round />
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl} title={recipeTitle} separator=":: ">
                  <WhatsappIcon size={32} round />
                </WhatsappShareButton>
                <EmailShareButton url={shareUrl} subject={recipe.recipe_name} body={`${recipeTitle}\n\n${shareUrl}`}>
                  <EmailIcon size={32} round />
                </EmailShareButton>
             </Box>
             {/* --- End Share Buttons --- */}
          </Box>

          <Typography variant="body1" paragraph><strong>Description:</strong> {recipe.description}</Typography>

          <Grid container spacing={2} sx={{ mb: 2 }}>
             <Grid item xs={6} sm={4}><Typography variant="body1"><strong>Cuisine:</strong> {recipe.cuisine}</Typography></Grid>
             <Grid item xs={6} sm={4}><Typography variant="body1"><strong>Prep Time:</strong> {recipe.prep_time} min</Typography></Grid>
             <Grid item xs={6} sm={4}><Typography variant="body1"><strong>Cook Time:</strong> {recipe.cook_time} min</Typography></Grid>
             <Grid item xs={6} sm={4}><Typography variant="body1"><strong>Servings:</strong> {recipe.servings}</Typography></Grid>
             <Grid item xs={6} sm={4}><Typography variant="body1"><strong>Difficulty:</strong> {recipe.difficulty}</Typography></Grid>
          </Grid>

          {/* Ingredients */}
          <Typography variant="h5" sx={{ marginTop: 2, fontFamily: "'Patrick Hand', cursive" }}>Ingredients</Typography>
          {recipe.ingredients.length > 0 ? (
            <ul>
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>
                  {ingredient.amount} {ingredient.measurement_unit} of {ingredient.ingredient_name}
                </li>
              ))}
            </ul>
          ) : (
            <Typography>No ingredients listed.</Typography>
          )}

          {/* Steps */}
          <Typography variant="h5" sx={{ marginTop: 2, fontFamily: "'Patrick Hand', cursive" }}>Steps</Typography>
          {recipe.steps.length > 0 ? (
            <ol>
              {recipe.steps.map((step, index) => (
                <li key={index} style={{ marginBottom: '8px' }}>{step.instruction}</li>
              ))}
            </ol>
          ) : (
            <Typography>No steps provided.</Typography>
          )}

          {/* Tags */}
          <Typography variant="h6" sx={{ marginTop: 2 }}>Tags</Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
            {recipe.tags.length > 0 ? (
                recipe.tags.map((tag, index) => (
                    <Chip key={index} label={tag} variant="outlined" />
                ))
            ) : (
                <Typography variant="body2" color="textSecondary">No tags available.</Typography>
            )}
          </Box>

          {/* Buttons */}
          <Grid container spacing={2} sx={{ marginTop: 4, justifyContent: 'center' }}>
            {(isOwner || isAdmin) && (
              <>
                <Grid item>
                  <Button variant="contained" color="primary" onClick={() => navigate(`/edit-recipe/${id}`)}>
                    Edit Recipe
                  </Button>
                </Grid>
                <Grid item>
                  <Button variant="contained" color="error" onClick={handleDelete}>
                    Delete Recipe
                  </Button>
                </Grid>
              </>
            )}
            <Grid item>
              <Button variant="outlined" onClick={() => navigate("/recipes")}>
                Back to Recipes
              </Button>
            </Grid>
          </Grid>

          {error && (
            <Typography color="error" sx={{ marginTop: 2, textAlign: 'center' }}>
              {error}
            </Typography>
          )}
        </CardContent>
      </Card>
    </>
  );
};

export default RecipeDetail;
